

const initState = [

];

export default (state = initState, {type, payload})=>{
    switch(type){
        case 'FETCH_PHONES_SUCCESS':
            const first = payload.split(' ').slice(1).join(' ');
            console.log(first);
            try {

                return JSON.parse(first)
            }
            catch(err){
                console.log(err);
                return state
            }


        default:
            return state
    }
}
